import React, { useEffect, useState } from 'react';
import './Hero.css';
import { motion } from 'framer-motion';

const Hero = () => {
  const [isVisible, setIsVisible] = useState(false);

  useEffect(() => {
    const timer = setTimeout(() => {
      setIsVisible(true);
    }, 300);

    return () => clearTimeout(timer);
  }, []);

  const scrollToContact = () => {
    const element = document.getElementById('contact');
    if (element) {
      element.scrollIntoView({ behavior: 'smooth' });
    }
  };

  return (
    <section id="home" className="hero">
      <div className="hero-content">
        <div className={`hero-text ${isVisible ? 'visible' : ''}`}>
          <h1>Residential assisted living, designed for you</h1>
          <p className="hero-subtitle tagline">Dignified care, personalized for you</p>
          <p className="hero-description">
            Experience compassionate care in our beautiful three-story home. 
            We provide 24-hour personalized care with the warmth and comfort of home.
          </p>
          <div className="hero-buttons">
            <button onClick={scrollToContact} className="btn btn-primary">
              Schedule a Tour
            </button>
            <a href="tel:651.983.1550" className="btn btn-secondary">
              Call Us Today
            </a>
          </div>
          <div className="hero-highlight">
            <span className="highlight-text">Limited units available</span>
          </div>
        </div>
        <div className={`hero-image ${isVisible ? 'visible' : ''}`}>
          <img 
            src="/images/happyelderlyfamily.png" 
            alt="Happy elderly family enjoying quality time together"
            className="hero-family-image"
            onError={(e) => {
              e.target.style.display = 'none';
              e.target.nextSibling.style.display = 'flex';
            }}
          />
          <div className="image-placeholder" style={{display: 'none'}}>
            <p>Happy elderly family enjoying quality time together</p>
          </div>
        </div>
      </div>
    </section>
  );
};

export default Hero;
